import { Component, OnInit } from '@angular/core';
import { Employee } from '../employee'
import { EmployeeService } from '../employee.service'
import { Router } from '@angular/router';
@Component({
  selector: 'app-employee-list',
  templateUrl: './employee-list.component.html',
  styleUrls: ['./employee-list.component.css']
})
export class EmployeeListComponent implements OnInit {

  employees!: Employee[];
  noDetailsFound = false;

  constructor(private employeeService: EmployeeService,
    private router: Router) { }

  ngOnInit(): void {
    this.getEmployees();
  }

  private getEmployees(){
    this.employeeService.getEmployeesList().subscribe((response:any) => {
      this.employees = response.data;
      if (response.status !== "OK") {
        this.noDetailsFound = true;
      }
    });
  }

  employeeDetails(id: number){
    this.router.navigate(['employee-details', id]);
  }

  updateEmployee(id: number){
    this.router.navigate(['update-employee', id]);
  }

  deleteEmployee(id: number){
    this.employeeService.deleteEmployee(id).subscribe( data => {
      console.log(data);
      this.getEmployees();
    })
  }
}
<h2> Employee List</h2>
<table class = "table table-striped">
    
    <thead>
        <tr>
            <th> ID</th>
            <th> Employee Name </th>
            <th> Age</th>
            <th> Designation</th>
            <th> Role</th>
            <th> Actions </th>
        </tr>
    </thead>
    
    <tbody>
        <tr *ngFor = "let employee of employees let i=index"  >
            <td> {{i+1}} </td>
            <td> {{ employee.employeeName }} </td>
            <td> {{ employee.age }} </td>
            <td> {{ employee.designation }} </td>
            <td> {{ employee.role }} </td>
            
            <td>
                <button (click) = "updateEmployee(employee.empId)" class = "btn btn-info"> Update</button>
                <button (click) = "deleteEmployee(employee.empId)" class = "btn btn-danger"> Delete</button>
                <button (click) = "employeeDetails(employee.empId)" class = "btn btn-info"> View</button>
            </td>

        </tr>
        <div *ngIf="noDetailsFound">
             <h6>No employee details found.</h6> 
          </div>
    </tbody>
</table>
